import React, { Component } from "react";
import { Modal, Button, Row, Col, Form } from "react-bootstrap";
import ErrorMessages from "../components/layout/ErrorMessages";
import { timehours } from "../helpers/timeschedule";
import axios from "axios";

class AddMeetingModal extends Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.isValidMeetingInput = this.isValidMeetingInput.bind(this);
    this.state = { meetingrooms: [], meetings: [], errorMessage: null };
  }

  componentDidMount() {
    axios
      .get("https://localhost:44388/api/MeetingRooms")
      .then((res) => this.setState({ meetingrooms: res.data }));
  }

  isValidMeetingInput(meetings, event) {
    let isValid = true;
    let date = event.target.date.value;
    let name = event.target.name.value;
    let start = event.target.start.value;
    let end = event.target.end.value;
    const found = meetings.find(
      (meeting) => meeting.date === date && meeting.date === date
    );

    if (parseInt(start) >= parseInt(end)) {
      this.setState({ errorMessage: "Meeting time slot is not valid" });
      isValid = false;
    }

    return isValid;
  }

  handleSubmit = (e) => {
    e.preventDefault();

    axios
      .get("https://localhost:44388/api/Bookings")
      .then((res) => this.setState({ meetings: res.data }));

    if (this.isValidMeetingImput(this.state.meetings, e)) {
      axios
        .post("https://localhost:44388/api/Bookings", {
          date: e.target.date.value,
          name: e.target.name.value,
          mail: e.target.mail.value,
          subject: e.target.subject.value,
          start: e.target.start.value,
          end: e.target.end.value,
          meetingRoomId: parseInt(e.target.meetingRoomId.value),
        })
        .catch((error) => {
          if (error.message !== null) {
            this.setState({
              errorMessage: error.message,
            });
          } else {
            this.setState({
              errorMessage: null,
            });
          }
        });
    }
  };

  render() {
    return (
      <Modal
        {...this.props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Add Meeting
          </Modal.Title>
        </Modal.Header>
        {this.state.errorMessage !== null ? (
          <ErrorMessages theMessage={this.state.errorMessage} />
        ) : null}
        <Modal.Body>
          <Row>
            <Col sm={6}>
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="date">
                  <Form.Label>Date</Form.Label>
                  <Form.Control
                    type="date"
                    name="date"
                    required
                    placeholder="Meeting Date"
                  />
                </Form.Group>
                <Form.Group controlId="name">
                  <Form.Label>Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    required
                    placeholder="Owner Name"
                  />
                </Form.Group>
                <Form.Group controlId="mail">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="text"
                    name="mail"
                    required
                    placeholder="Email"
                  />
                </Form.Group>
                <Form.Group controlId="subject">
                  <Form.Label>Subject</Form.Label>
                  <Form.Control
                    type="text"
                    name="subject"
                    required
                    placeholder="Subject"
                  />
                </Form.Group>
                <Form.Group controlId="start">
                  <Form.Label>Start hour time</Form.Label>
                  <Form.Control as="select">
                    {timehours.map((time) => (
                      <option key={time}>{time}</option>
                    ))}
                  </Form.Control>
                </Form.Group>
                <Form.Group controlId="end">
                  <Form.Label>End hourtime</Form.Label>
                  <Form.Control as="select">
                    {timehours.map((time) => (
                      <option key={time}>{time}</option>
                    ))}
                  </Form.Control>
                </Form.Group>
                <Form.Group controlId="meetingRoomId">
                  <Form.Label>Meeting room</Form.Label>
                  <Form.Control as="select">
                    {this.state.meetingrooms.map((room) => (
                      <option key={room.id} value={room.id}>
                        {room.name}
                      </option>
                    ))}
                  </Form.Control>
                </Form.Group>
                <Form.Group>
                  <Button variant="primary" type="submit">
                    Add meeting
                  </Button>
                </Form.Group>
              </Form>
            </Col>
          </Row>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="danger" onClick={this.props.onHide}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    );
  }
}

export default AddMeetingModal;
