import React, { Component } from "react";
import { Table, Button, ButtonToolbar } from "react-bootstrap";
import AddMeetingModal from "./AddMeetingModal";
import axios from "axios";

class Meetings extends Component {
  constructor(props) {
    super(props);
    this.state = { meetings: [], addModalShow: false };
  }

  componentDidMount() {
    axios
      .get("https://localhost:44388/api/Bookings")
      .then((res) => this.setState({ meetings: res.data }));
  }

  componentDidUpdate() {
    axios
      .get("https://localhost:44388/api/Bookings")
      .then((res) => this.setState({ meetings: res.data }));
  }

  //Delete meeting
  delMeeting = (id) => {
    if (window.confirm("Are you sure ?")) {
      axios.delete(`https://localhost:44388/api/Bookings/${id}`).then((res) =>
        this.setState({
          meetings: [
            ...this.state.meetings.filter((meeting) => meeting.id !== id),
          ],
        })
      );
    }
  };

  render() {
    let addModalClose = () => this.setState({ addModalShow: false });
    return (
      <div>
        <Table className="mt-4" striped bordered hover size="sm">
          <thead>
            <tr>
              <th>MeetingId</th>
              <th>Date</th>
              <th>Name</th>
              <th>Mail</th>
              <th>Subject</th>
              <th>Start</th>
              <th>End</th>
              <th>Meeting Room</th>
              <th>Option</th>
            </tr>
          </thead>
          <tbody>
            {this.state.meetings.map((meeting) => (
              <tr key={meeting.id}>
                <td>{meeting.id}</td>
                <td>{meeting.date}</td>
                <td>{meeting.name}</td>
                <td>{meeting.mail}</td>
                <td>{meeting.subject}</td>
                <td>{meeting.start}</td>
                <td>{meeting.end}</td>
                <td>{meeting.meetingRoomId}</td>
                <td>
                  <ButtonToolbar>
                    <Button
                      className="mr-2"
                      variant="danger"
                      onClick={() => this.delMeeting(meeting.id)}
                    >
                      Delete
                    </Button>
                  </ButtonToolbar>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        <ButtonToolbar>
          <Button
            variant="primary"
            onClick={() => this.setState({ addModalShow: true })}
          >
            Add Meeting
          </Button>

          <AddMeetingModal
            show={this.state.addModalShow}
            onHide={addModalClose}
          />
        </ButtonToolbar>
      </div>
    );
  }
}

export default Meetings;
