import React, { Component } from "react";
import { Table } from "react-bootstrap";
import MeetingItem from "./MeetingItem";
import AddMeeting from "./AddMeeting";
import axios from "axios";

class Meetings extends Component {
  constructor(props) {
    super(props);
    this.state = { meetings: [] };
  }

  componentDidMount() {
    axios
      .get("https://localhost:44388/api/Bookings")
      .then((res) => this.setState({ meetings: res.data }));
  }

  //Delete meeting
  delMeeting = (id) => {
    axios.delete(`https://localhost:44388/api/Bookings/${id}`).then((res) =>
      this.setState({
        meetings: [
          ...this.state.meetings.filter((meeting) => meeting.id !== id),
        ],
      })
    );
  };

  //Add meeting
  addMeeting = (date, name, mail, subject, start, end, meetingRoomId) => {
    axios
      .post("https://localhost:44388/api/Bookings", {
        date: date,
        name: name,
        mail: mail,
        subject: subject,
        start: start,
        end: end,
        meetingRoomId: meetingRoomId,
      })
      .then((res) =>
        this.setState({ meetings: [...this.state.meetings, res.data] })
      );
  };

  render() {
    return (
      <Table className="mt-4" striped bordered hover size="sm">
        <thead>
          <tr>
            <th>RoomId</th>
            <th>Name</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {this.state.meetingRooms.map((meetingroom) => (
            <tr key={meetingroom.id}>
              <td>{meetingroom.id}</td>
              <td>{meetingroom.name}</td>
              <td>{meetingroom.description}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    );
  }
}

export default Meetings;
