import React, { Component } from "react";
import MeetingItem from "./MeetingItem";

class Meetings extends Component {
  constructor(props) {
    super(props);
    this.state = { meetings: [] };
  }

  componentDidMount() {
    axios
      .get("https://localhost:44388/api/Bookings")
      .then((res) => this.setState({ meetings: res.data }));
  }

  //Delete meeting
  delMeeting = (id) => {
    axios.delete(`https://localhost:44388/api/Bookings/${id}`).then((res) =>
      this.setState({
        meetings: [
          ...this.state.meetings.filter((meeting) => meeting.id !== id),
        ],
      })
    );
  };

  //Add meeting
  addMeeting = (date, name, mail, subject, start, end, meetingRoomId) => {
    axios
      .post("https://localhost:44388/api/Bookings", {
        date: date,
        name: name,
        mail: mail,
        subject: subject,
        start: start,
        end: end,
        meetingRoomId: meetingRoomId,
      })
      .then((res) =>
        this.setState({ meetings: [...this.state.meetings, res.data] })
      );
  };
  render() {
    return this.state.meetings.map((meeting) => (
      <MeetingItem
        key={meeting.id}
        meeting={meeting}
        delMeeting={this.props.delMeeting}
      />
    ));
  }
}

export default Meetings;
