import React, { Component } from "react";
import { Table, Button, ButtonToolBar } from "react-bootstrap";
import AddMeetingModal from "./AddMeetingModal";
import axios from "axios";

class Meetings extends Component {
  constructor(props) {
    super(props);
    this.state = { meetings: [], addModalShow: false };
  }

  componentDidMount() {
    axios
      .get("https://localhost:44388/api/Bookings")
      .then((res) => this.setState({ meetings: res.data }));
  }

  //Delete meeting
  delMeeting = (id) => {
    axios.delete(`https://localhost:44388/api/Bookings/${id}`).then((res) =>
      this.setState({
        meetings: [
          ...this.state.meetings.filter((meeting) => meeting.id !== id),
        ],
      })
    );
  };

  //Add meeting
  addMeeting = (date, name, mail, subject, start, end, meetingRoomId) => {
    axios
      .post("https://localhost:44388/api/Bookings", {
        date: date,
        name: name,
        mail: mail,
        subject: subject,
        start: start,
        end: end,
        meetingRoomId: meetingRoomId,
      })
      .then((res) =>
        this.setState({ meetings: [...this.state.meetings, res.data] })
      );
  };

  render() {
    const { meetings } = this.state;
    let addModalClose = () => this.setState({ addModalShow: false });
    return (
      <Table className="mt-4" striped bordered hover size="sm">
        <thead>
          <tr>
            <th>MeetingId</th>
            <th>Date</th>
            <th>Name</th>
            <th>Mail</th>
            <th>Subject</th>
            <th>Start</th>
            <th>End</th>
            <th>Meeting Room</th>
          </tr>
        </thead>
        <tbody>
          {this.state.meetings.map((meeting) => (
            <tr key={meeting.id}>
              <td>{meeting.id}</td>
              <td>{meeting.date}</td>
              <td>{meeting.name}</td>
              <td>{meeting.mail}</td>
              <td>{meeting.subject}</td>
              <td>{meeting.start}</td>
              <td>{meeting.end}</td>
              <td>{meeting.meetingRoomId}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    );
  }
}

export default Meetings;
