import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import AppHeader from "./components/layout/AppHeader";
import AppFooter from "./components/layout/AppFooter";
import Navigation from "./components/layout/Navigation";
import MeetingRooms from "./components/MeetingRooms";
import Meetings from "./components/Meetings";
import axios from "axios";

function App() {
  const [meetingRooms, setMeetingRooms] = useState([]);
  const [meetings, setMeetings] = useState([]);

  useEffect(() => {
    const fetchMeetingRooms = () => {
      const resMeetingRooms = axios.get(
        "https://localhost:44388/api/MeetingRooms"
      );
      setMeetingRooms(resMeetingRooms.data);
    };
    const fetchMeetings = () => {
      const resMeetings = axios.get("https://localhost:44388/api/Bookings");
      setMeetings(resMeetings.data);
    };

    fetchMeetingRooms();
    fetchMeetings();
  }, [meetings]);

  //Add meeting
  addMeeting = (date, name, mail, subject, start, end, meetingRoomId) => {
    axios
      .post("https://localhost:44388/api/Bookings", {
        date: date,
        name: name,
        mail: mail,
        subject: subject,
        start: start,
        end: end,
        meetingRoomId: meetingRoomId,
      })
      .then((res) => setMeetings({ meetings: [...meetings, res.data] }));
  };

  //Delete meeting
  delMeeting = (id) => {
    axios.delete(`https://localhost:44388/api/Bookings/${id}`).then((res) =>
      setMeetings({
        meetings: [...meetings.filter((meeting) => meeting.id !== id)],
      })
    );
  };

  return (
    <BrowserRouter>
      <div className="container">
        <AppHeader />
        <Navigation />

        <Switch>
          <Route
            path="/"
            render={(props) => <MeetingRooms meetingRooms={meetingRooms} />}
          />
          <Route
            path="/meetingrooms"
            render={(props) => <MeetingRooms meetingRooms={meetingRooms} />}
          />
          <Route
            path="/meetings"
            render={(props) => (
              <Meetings
                meetings={meetings}
                addMeeting={addMeeting}
                delMeeting={delMeeting}
              />
            )}
          />
        </Switch>

        <AppFooter />
      </div>
    </BrowserRouter>
  );
}

export default App;
