import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import AppHeader from "./components/layout/AppHeader";
import AppFooter from "./components/layout/AppFooter";
import Navigation from "./components/layout/Navigation";
import MeetingRooms from "./components/MeetingRooms";
import Meetings from "./components/Meetings";
import axios from "axios";

function App() {
  const [meetingRooms, setMeetingRooms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [meetings, setMeetings] = useState([]);

  useEffect(() => {
    const fetchMeetingRooms = async () => {
      setLoading(true);
      const res = axios.get("https://localhost:44388/api/MeetingRooms");
      setMeetingRooms(res.data);
      setLoading(false);
    };
    const fetchMeetings = async () => {
      setLoading(true);
      const res = axios.get("https://localhost:44388/api/Bookings");
      setMeetings(res.data);
      setLoading(false);
    };

    fetchMeetingRooms();
    fetchMeetings();
  }, []);

  //Add meeting
  addMeeting = (title) => {
    axios
      .post("https://localhost:44388/api/Bookings", {
        title,
        completed: false,
      })
      .then((res) => setMeetings({ meetings: [...meetings, res.data] }));
  };

  //Delete meeting
  delTodo = (id) => {
    axios
      .delete(`https://jsonplaceholder.typicode.com/todos/${id}`)
      .then((res) =>
        this.setState({
          todos: [...this.state.todos.filter((todo) => todo.id !== id)],
        })
      );
  };

  return (
    <BrowserRouter>
      <div className="container">
        <AppHeader />
        <Navigation />

        <Switch>
          <Route
            path="/meetingrooms"
            render={(props) => (
              <MeetingRooms meetingRooms={meetingRooms} loading={loading} />
            )}
          />
          <Route
            path="/meetings"
            render={(props) => (
              <Meetings meetings={meetingRooms} loading={loading} />
            )}
          />
        </Switch>

        <AppFooter />
      </div>
    </BrowserRouter>
  );
}

export default App;
