import React, { Component } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import AppHeader from "./components/layout/AppHeader";
import AppFooter from "./components/layout/AppFooter";
import Navigation from "./components/layout/Navigation";
import MeetingRooms from "./components/MeetingRooms";
import Meetings from "./components/Meetings";
import axios from "axios";

export default class App extends Component {
  //const [meetingRooms, setMeetingRooms] = useState([]);
  //const [meetings, setMeetings] = useState([]);
  state = {
    meetingRooms: [],
    meetings: [],
  };

  componentDidMount() {
    axios
      .get("https://localhost:44388/api/MeetingRooms")
      .then((res) => this.setState({ meetingRooms: res.data }));
    axios
      .get("https://localhost:44388/api/Bookings")
      .then((res) => this.setState({ meetings: res.data }));
  }

  //Add meeting
  addMeeting = (date, name, mail, subject, start, end, meetingRoomId) => {
    axios
      .post("https://localhost:44388/api/Bookings", {
        date: date,
        name: name,
        mail: mail,
        subject: subject,
        start: start,
        end: end,
        meetingRoomId: meetingRoomId,
      })
      .then((res) =>
        this.setState({ meetings: [...this.state.meetings, res.data] })
      );
  };

  render() {
    return (
      <BrowserRouter>
        <div className="container">
          <AppHeader />
          <Navigation />

          <Switch>
            <Route
              path="/"
              render={(props) => (
                <MeetingRooms meetingRooms={this.state.meetingRooms} />
              )}
            />
            <Route
              path="/meetingrooms"
              render={(props) => (
                <MeetingRooms meetingRooms={this.state.meetingRooms} />
              )}
            />
            <Route
              path="/meetings"
              render={(props) => (
                <Meetings
                  meetings={this.state.meetings}
                  addMeeting={this.addMeeting}
                  delMeeting={this.delMeeting}
                />
              )}
            />
          </Switch>

          <AppFooter />
        </div>
      </BrowserRouter>
    );
  }
}
