import React, { Component } from "react";
import { Modal, Button, Row, Col, Form } from "react-bootstrap";
import { timehours } from "../helpers/timeschedule";
import axios from "axios";

class AddMeetingModal extends Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = { meetingrooms: [], meetings: [], errorMessage: null };
  }

  componentDidMount() {
    axios
      .get("https://localhost:44388/api/MeetingRooms")
      .then((res) => this.setState({ meetingrooms: res.data }));
  }

  handleSubmit = (e) => {
    e.preventDefault();

    axios.post("https://localhost:44388/api/Bookings", {
      date: e.target.date.value,
      name: e.target.name.value,
      email: e.target.email.value,
      subject: e.target.subject.value,
      start: e.target.start.value,
      end: e.target.end.value,
      meetingRoomId: parseInt(e.target.meetingRoomId.value),
    });
  };

  render() {
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Modal heading
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Centered Modal</h4>
          <p>
            Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
            dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta
            ac consectetur ac, vestibulum at eros.
          </p>
        </Modal.Body>
      </Modal>
    );
  }
}

export default AddMeetingModal;
